mapper.select:
select user_id, nickname, cash_balance
from t_user
where user_id = #{user_id}

select game_id, game_name, price, discount_rate
from t_game
where game_id = #{GAME_ID}

mapper.update:
update t_user 
set cash_balance = #{cash_balance}, bought_games = bought_games + 1 
where user_id = #{user_id}

update t_game 
set sold = sold + 1 
where game_id = #{game_id}

mapper.select:
select order_id, game_id, game_name, user_id, nickname, order_date, total_amount
from (
	select a.*, rownum rn 
	from ( 
	select * from v_order 
	where user_id = #{user_id}
	order by order_id desc 
	) a
)
where rn between #{startRow} and #{endRow}

select user_id 
from v_order
where game_id = #{GAME_ID}

select order_id, nickname, game_name, total_amount, order_date 
from v_order
where order_id = #{ORDER_ID}
